mapper.update:
UPDATE product 

prod_cnt = prod_cnt - #{tranCnt}
 
WHERE prod_no = #{purchaseProd.prodNo}

UPDATE transaction

   tran_status_code = #{tranCode:VARCHAR}

 WHERE tran_no = #{tranNo}
   AND prod_no = #{purchaseProd.prodNo}

UPDATE product

  	prod_cnt = prod_cnt + #{tranCnt:INTEGER}

 WHERE prod_no = #{purchaseProd.prodNo}

UPDATE transaction
payment_option = #{paymentOption:VARCHAR}, receiver_name = #{receiverName:VARCHAR}, receiver_phone = #{receiverPhone:VARCHAR}, dlvy_request = #{divyRequest:VARCHAR}, dlvy_date = #{divyDate:DATE}, dlvy_postcode = #{divyPostcode:VARCHAR}, dlvy_address = #{divyAddress:VARCHAR}, dlvy_address2 = #{divyAddress2:VARCHAR}
WHERE tran_no = #{tranNo}

UPDATE transaction

	score = #{score:INTEGER},
	comment_text = #{commentText:VARCHAR},
	comment_date = SYSDATE,
	comment_image = #{commentImage:VARCHAR}

WHERE tran_no = #{tranNo}
  AND prod_no = #{purchaseProd.prodNo}

mapper.select:

AND t.tran_no = #{tranNo}
AND p.prod_no = #{prodNo}
